      // \u5148\u786e\u5b9a\u4e00\u4e2a\u503c nums[i]\n        // \u518d\u53bb\u627e\u53e6\u5916\u4e24\u4e2a\u503c nums[left] \u548c nums[right]\n        // \u9700\u8981\u6ee1\u8db3 nums[i] < nums[left] < nums[right]\n        target := -nums[i]\n        left, right := i + 1, len(nums) - 1\n\n        // \u4f7f\u7528\u53cc\u6307\u9488\u6cd5\u786e\u5b9a\u5269\u4e0b\u4e24\u4e2a\u503c\n        for left < right {\n            sum := nums[left] + nums[right]\n            if sum < target {\n                left++\n            } else if sum > target {\n                right--\n            } else if sum == target {\n                ans = append(ans, []int{nums[i], nums[left], nums[right]})\n                // \u627e\u5230\u76ee\u6807\u503c\uff0c\u5de6\u53f3\u6307\u9488\u5206\u522b\u79fb\u52a8\u4e00\u4f4d\n                left++\n                right--\n            }\n        }\n    }\n    return ans\n}\n")),(0,u.kt)("p",null,"\u4e3b\u8981\u96be\u70b9\u5728\u4e8e\u5982\u4f55\u53bb\u6389\u91cd\u590d\u7684\u7ed3\u679c\uff0c\u5b8c\u6574\u89e3\u6cd5\u5982\u4e0b\uff1a"),(0,u.kt)("pre",null,(0,u.kt)("code",{parentName:"pre",className:"language-go"},'package algorithm\n\nimport (\n    "sort"\n)\n\nfunc ThreeSum(nums []int) [][]int {\n    ans := make([][]int, 0)\n    // \u6570\u7ec4\u5143\u7d20\u4e2a\u6570\u5c0f\u4e8e 3\uff0c\u76f4\u63a5\u8fd4\u56de\n    if len(nums) < 3 {\n        return ans\n    }\n    // \u6392\u5e8f\n    sort.Ints(nums)\n    // \u904d\u5386\u5230\u5012\u6570\u7b2c\u4e8c\u4e2a\uff0c\u56e0\u4e3a\u662f\u4e09\u4e2a\u6570\u603b\u548c\n    for i := 0; i < len(nums) - 2; i++ {\n        // \u89c4\u5b9a nums[i] < nums[left] < nums[right]\n        // \u5982\u679c nums[i] > 0 \u5219\u4e0d\u5b58\u5728\u53e6\u5916\u4e24\u4e2a\u503c\u4f7f\u5f97\u76f8\u52a0\u7b49\u4e8e 0\n        // \u5927\u4e8e 0 \u53ef\u4ee5\u76f4\u63a5\u8df3\u51fa\u5faa\u73af\u4e86\n        if nums[i] > 0 {\n            break\n        }\n        // \u8fc7\u6ee4 nums[i] \u91cd\u590d\n        if i > 0 && nums[i] == nums[i-1] {\n            continue\n        }\n        // \u5148\u786e\u5b9a\u4e00\u4e2a\u503c nums[i]\n        // \u518d\u53bb\u627e\u53e6\u5916\u4e24\u4e2a\u503c nums[left] \u548c nums[right]\n        // \u9700\u8981\u6ee1\u8db3 nums[i] < nums[left] < nums[right]\n        target := -nums[i]\n        left, right := i + 1, len(nums) - 1\n\n        // \u4f7f\u7528\u53cc\u6307\u9488\u6cd5\u786e\u5b9a\u5269\u4e0b\u4e24\u4e2a\u503c\n        for left < right {\n            sum := nums[left] + nums[right]\n            if sum < target {\n                left++\n            } else if sum > target {\n                right--\n            } else if sum == target {\n                ans = append(ans, []int{nums[i], nums[left], nums[right]})\n                // \u627e\u5230\u76ee\u6807\u503c\uff0c\u5de6\u53f3\u6307\u9488\u5206\u522b\u79fb\u52a8\u4e00\u4f4d\n                left++\n                right--\n                // \u8fc7\u6ee4 nums[left] \u91cd\u590d\n                for left < right && nums[left] == nums[left-1] {\n                    left++\n                }\n                // \u8fc7\u6ee4 nums[right] \u91cd\u590d\n                for left < right && nums[right] == nums[right+1] {\n                    right--\n                }\n            }\n        }\n    }\n    return ans\n}\n')))}p.isMDXComponent=!0}}]);